mapper.select:
    SELECT DISTINCT * from (
		SELECT
		    t1.ID_,
			t1.COURSE_CODE_,
			t1.COURSE_NAME_,
			t1.COURSE_NAME_EN_,
			t1.TRAINING_LEVEL_,
			t1.CREDITS_,
			t1.PERIOD_,
		    IFNULL(t2.ID_,0) suggestStatus
		FROM
			course_open_t t1
		LEFT JOIN elc_course_suggest_switch_t t2 ON t1.COURSE_CODE_ = t2.COURSE_CODE_

			and t1.IS_OPEN_ = 1

		        and t1.CALENDAR_ID_  = #{calendarId}


		        and (t1.COURSE_CODE_ like concat(#{keyWord},'%') or t1.COURSE_NAME_ like concat(#{keyWord},'%') or t1.COURSE_NAME_EN_ like concat(#{keyWord},'%'))


		        and t1.TRAINING_LEVEL_  = #{trainingLevel}


		        and t1.FACULTY_  = #{faculty}


		        and t1.NATURE_  = #{nature}


		        and t1.CREDITS_  = #{credits}


		        and t1.PERIOD_  = #{period}


		        and t1.PROJ_ID_  = #{projectId}


		        and t1.COURSE_CODE_ in

		          #{courseCode}



		)t3 


		        and t3.suggestStatus  = 0


		        and t3.suggestStatus  > 0

select a.CALENDAR_ID_ calendarId,a.IS_OPEN_ isOpen,a.COURSE_CODE_ courseCode, a.COURSE_NAME_ courseName, a.TRAINING_LEVEL_ trainingLevel,a.CREDITS_ credits, b.NATURE_ nature,b.ASSESSMENT_MODE_ assessmentMode from course_open_t a join course_t b on b.CODE_ = a.COURSE_CODE_ where a.COURSE_CODE_ in
#{item}
and a.CALENDAR_ID_ = #{calendarId}

select a.COURSE_NAME_ courseName,a.FACULTY_ faculty,a.CREDITS_ credits, c.ID_ teachingClassId,c.CODE_ classCode,c.CAMPUS_ campus,c.REMARK_ remark, c.ELC_NUMBER_ selectNumber,c.NUMBER_ number,d.TEACHER_NAME_ teacherName from course_open_t a join teaching_task_t b on b.COURSE_OPEN_ID_ = a.ID_ join teaching_class_t c on c.TASK_ID_ = b.ID_ join teaching_class_teacher_t d on d.TEACHING_CLASS_ID_ = c.ID_ where a.COURSE_CODE_ in
#{item}
and a.CALENDAR_ID_ = #{calendarId}
and (st.STUDENT_CODE_ like CONCAT('%', #{keyWord}, '%') or st.NAME_ like CONCAT('%', #{keyWord}, '%'))
