mapper.select:
SELECT t1.ID_, t1.STUDENT_ID_, t1.PROJECT_ID_, t1.ORIGS_COURSE_ID_, t1.SUB_COURSE_ID_, t4.NAME_, t4.GRADE_, t4.FACULTY_, t4.PROFESSION_, t2.CODE_ origsCourseCode, t2.NAME_ origsCourseName, t2.CREDITS_ origsCredits, t3.CODE_ subCourseCode, t3.NAME_ subCourseName, t3.CREDITS_ subCredits FROM elc_cou_subs_t t1 JOIN course_t t2 ON t1.ORIGS_COURSE_ID_ = t2.ID_ JOIN course_t t3 ON t1.SUB_COURSE_ID_ = t3.ID_ JOIN student_t t4 ON t1.student_ID_ = t4.STUDENT_CODE_
and t1.STUDENT_ID_ in
#{studentId}
and t1.STUDENT_ID_ = #{studentId}
and t4.LEAVE_SCHOOL_ = 1
and EXISTS (select XH from tj_ungraduate where t4.STUDENT_CODE_ = XH)
and EXISTS (select XH from tj_ungraduate_foreign where t4.STUDENT_CODE_ = XH)
and t4.GRADE_ = #{grade}
and t4.FACULTY_ = #{faculty}
and t4.PROFESSION_ = #{profession}
and t2.CODE_ =#{origsCourseCode}
and t2.NAME_ = #{origsCourseName}
and t3.CODE_ =#{subCourseCode}
and t3.NAME_ = #{subCourseName}
and ( t4.STUDENT_CODE_ like concat ('%', #{keyword},'%') or t4.NAME_ like concat ('%', #{keyword},'%'))
ORDER BY t1.CREATED_AT_ DESC

select DISTINCT t2.CODE_ code,t2.ID_ id,t2.NAME_ name,t2.ENABLED_ enabled,t2.CREDITS_ credits,t2.NAME_EN_ nameEn, t2.PERIOD_ period,t2.COLLEGE_ college from student_undergraduate_score_info_t t1 left join course_t t2 on t1.COURSE_CODE_ = t2.CODE_
and t1.STUDENT_NUM_ = #{studentId}
and ( t2.CODE_ like concat ('%', #{keyword},'%') or t2.NAME_ like concat ('%', #{keyword},'%'))
