mapper.select:
	select
    t1.*,
    t2.NAME_,t2.PROFESSION_,
    t3.NAME_ as courseName, t3.CREDITS_,
    t4.CODE_ as teachingClassCode,
    t4.CAMPUS_
    from elc_course_takes_t t1
    left join student_t t2 ON t2.STUDENT_CODE_ = t1.STUDENT_ID_
    left join course_t t3 ON t3.CODE_ = t1.COURSE_CODE_
    join teaching_class_t t4 ON t4.ID_ = t1.TEACHING_CLASS_ID_

	 t1.CALENDAR_ID_ =#{query.calendarId,jdbcType=BIGINT}

	 and t1.STUDENT_ID_ = #{query.studentId}


	 and t1.COURSE_TAKE_TYPE_ = #{query.courseTakeType}


	 and t2.NAME_ = #{query.stuName}


	 and t2.FACULTY_ = #{query.faculty}


	 and t2.PROFESSION_ = #{query.profession}


	 and t3.CODE_ = #{query.courseCode}


	 and t4.CODE_ = #{query.teachingClassCode}


          and t1.MODE_ = #{query.mode}

  	select 
  		class.id_ as TEACHING_CLASS_ID_ , class.code_ as teachingClassCode, 
  		cour.id_ as COURSE_ID_, open.COURSE_CODE_, open.COURSE_NAME_ as courseName
	from teaching_class_t class
	left join teaching_task_t task ON task.id_ = class.TASK_ID_
	left join course_open_t open ON open.id_ = task.COURSE_OPEN_ID_
	left join course_t cour ON cour.code_ = open.COURSE_CODE_

		open.CALENDAR_ID_ = #{calendarId}

			and class.id_ = #{teachingClassId}


			and class.code_ = #{code}

SELECT COUNT(ID_) FROM `elc_course_takes_t` WHERE COURSE_CODE_ =#{courseCode,jdbcType=VARCHAR}
and STUDENT_ID_ =#{studentCode}
and CALENDAR_ID_ =#{calendarId,jdbcType=BIGINT}

SELECT el.ID_ id,el.CALENDAR_ID_ calendarId,st.STUDENT_CODE_ studentCode,st.NAME_ studentName,co.CODE_ code,co.NAME_ codeName,co.NATURE_ nature,co.CREDITS_ credits,
  te.COURSE_LABEL_ID_ label,el.PAID_ paid,te.WEEK_HOUR_ weekHour,el.COURSE_TAKE_TYPE_ courseTakeType,el.MODE_ mode,el.TURN_ turn,el.CHOOSE_OBJ_ chooseObj
,te.END_WEEK_ endWeek,te.START_WEEK_ startWeek,te.PERIOD_ period,te.ID_ teachingClassId,te.CODE_ teachingClassCode
FROM elc_course_takes_t el
JOIN course_t co on el.COURSE_CODE_=co.CODE_
JOIN student_t st on el.STUDENT_ID_=st.STUDENT_CODE_
JOIN teaching_class_t te on el.TEACHING_CLASS_ID_=te.ID_



            st.STUDENT_CODE_ not in (select STUDENT_ID_ from elc_no_graduate_stds_t)


            st.STUDENT_CODE_ in  (select STUDENT_ID_ from elc_no_graduate_stds_t t1 join student_t t2 on t1.STUDENT_ID_=t2.STUDENT_CODE_ where t2.IS_OVERSEAS_ =0 )


            st.STUDENT_CODE_ in  (select STUDENT_ID_ from elc_no_graduate_stds_t t1 join student_t t2 on t1.STUDENT_ID_=t2.STUDENT_CODE_ where t2.IS_OVERSEAS_ =1)



    and el.COURSE_TAKE_TYPE_=2 and el.PAID_=0


        and el.CALENDAR_ID_ = #{calendarId,jdbcType=BIGINT}


        and st.STUDENT_CODE_ like CONCAT('%', #{studentCode,jdbcType=VARCHAR}, '%')


        and te.CODE_ like CONCAT('%', #{classCode,jdbcType=VARCHAR}, '%')


        and st.NAME_ like CONCAT('%', #{studentName,jdbcType=VARCHAR}, '%')


        and st.GRADE_ =#{grade,jdbcType=INTEGER}


        and st.TRAINING_LEVEL_ =#{trainingLevel,jdbcType=VARCHAR}


        and st.STUDENT_CATEGORY_ =#{studentCategory,jdbcType=VARCHAR}


        and st.FACULTY_ =#{faculty,jdbcType=VARCHAR}


        and st.PROFESSION_ =#{profession,jdbcType=VARCHAR}


        and st.RESEARCH_DIRECTION_ =#{researchDirection,jdbcType=VARCHAR}


        and co.COLLEGE_ =#{openFaculty,jdbcType=VARCHAR}


        and co.LABEL_ like CONCAT('%', #{label,jdbcType=VARCHAR}, '%')


        and co.CODE_ like CONCAT('%', #{code,jdbcType=VARCHAR}, '%')


        and co.NAME_ like CONCAT('%', #{name,jdbcType=VARCHAR}, '%')

SELECT  el.CALENDAR_ID_ calendarId,st.STUDENT_CODE_ studentCode,st.NAME_ name,st.SEX_ sex,
st.GRADE_  grade, st.FACULTY_ faculty,st.PROFESSION_ profession,st.TRAINING_LEVEL_ trainingLevel,st.STUDENT_CATEGORY_ studentCategory,COUNT(el.ID_) rebuildNumber FROM `elc_course_takes_t` el
LEFT JOIN student_t st on st.STUDENT_CODE_=el.STUDENT_ID_
  LEFT JOIN teaching_class_t te on el.TEACHING_CLASS_ID_=te.ID_


    and el.COURSE_TAKE_TYPE_=2 and el.PAID_=0


        and el.CALENDAR_ID_ = #{calendarId,jdbcType=BIGINT}


        and st.STUDENT_CODE_ like CONCAT('%', #{studentCode,jdbcType=VARCHAR}, '%')


        and te.CODE_ like CONCAT('%', #{classCode,jdbcType=VARCHAR}, '%')


        and st.NAME_ like CONCAT('%', #{studentName,jdbcType=VARCHAR}, '%')


        and st.GRADE_ =#{grade,jdbcType=INTEGER}


        and st.TRAINING_LEVEL_ =#{trainingLevel,jdbcType=VARCHAR}


        and st.STUDENT_CATEGORY_ =#{studentCategory,jdbcType=VARCHAR}


        and st.FACULTY_ =#{faculty,jdbcType=VARCHAR}


        and st.PROFESSION_ =#{profession,jdbcType=VARCHAR}


        and st.RESEARCH_DIRECTION_ =#{researchDirection,jdbcType=VARCHAR}


GROUP BY el.STUDENT_ID_

SELECT el.ID_ id,el.CALENDAR_ID_ calendarId,el.TEACHING_CLASS_ID_ teachingClassId,cl.CODE_ calssCode,co.CODE_ courseCode,co.NAME_ courseName,cl.ELC_NUMBER_ selectCourseNumber,cl.NUMBER_ numberLimit,co.LABEL_ label,co.COLLEGE_ faculty FROM `elc_course_takes_t` el
LEFT JOIN course_t co on co.CODE_=el.COURSE_CODE_
LEFT JOIN teaching_class_t cl on cl.ID_=el.TEACHING_CLASS_ID_
/*LEFT JOIN (select COURSE_ID_,COUNT(ID_) NUMBER_ from elc_course_takes_t GROUP BY COURSE_ID_) ss on ss.COURSE_ID_=el.COURSE_ID_*/


    and el.CALENDAR_ID_ =#{calendarId,jdbcType=BIGINT}


        and co.COLLEGE_ =#{faculty,jdbcType=VARCHAR}


        and co.TRAINING_LEVEL_ =#{trainingLevel,jdbcType=VARCHAR}


        and cl.CAMPUS_ =#{campus,jdbcType=VARCHAR}


        and co.FORM_LEARNING_ =#{formLearning,jdbcType=VARCHAR}



GROUP BY el.TEACHING_CLASS_ID_,el.CALENDAR_ID_

SELECT st.STUDENT_CODE_ studentCode,st.NAME_ name,st.SEX_ sex,st.GRADE_ grade,st.FACULTY_ faculty,st.TRAINING_LEVEL_ trainingLevel,el.COURSE_TAKE_TYPE_ courseTakeType FROM `elc_course_takes_t` el
LEFT JOIN student_t st on el.STUDENT_ID_=st.STUDENT_CODE_
where TEACHING_CLASS_ID_= #{id,jdbcType=BIGINT}

SELECT te.ID_ teachingClassId, te.CODE_ classCode, te.NAME_ className, te.CAMPUS_ campus, co.COURSE_CODE_ courseCode, co.COURSE_NAME_ courseName, ro.ARRANGE_TIME_ID_ timeId, ti.DAY_OF_WEEK_ dayOfWeek, ti.TIME_START_ timeStart, ti.TIME_END_ timeEnd, ro.ROOM_ID_ roomID, ro.TEACHER_CODE_ teacherCode, GROUP_CONCAT(ro.WEEK_NUMBER_ ORDER BY ro.WEEK_NUMBER_ asc) weekNumberStr FROM teaching_class_t te join teaching_task_t ta on te.TASK_ID_=ta.ID_ left join course_open_t co on ta.COURSE_OPEN_ID_=co.ID_ join teaching_class_arrange_time_t ti on te.ID_=ti.TEACHING_CLASS_ID_ JOIN teaching_class_arrange_room_t ro ON ti.ID_ = ro.ARRANGE_TIME_ID_ WHERE te.ID_ in
#{id}
GROUP BY ro.ARRANGE_TIME_ID_,ti.TIME_START_,ti.TIME_END_,ro.ROOM_ID_ ,ro.TEACHER_CODE_

SELECT el.CALENDAR_ID_ calendarId,st.STUDENT_CODE_ studentCode,st.NAME_ name,st.GRADE_ grade,st.FACULTY_ faculty,st.PROFESSION_ profession,
st.TRAINING_LEVEL_ trainingLevel,st.FORM_LEARNING_ formLearning FROM `elc_course_takes_t` el
LEFT JOIN student_t st on el.STUDENT_ID_=st.STUDENT_CODE_


        and el.CALENDAR_ID_ = #{calendarId,jdbcType=BIGINT}


        and (st.STUDENT_CODE_ like concat('%',#{condition},'%') or st.NAME_ like concat('%',#{condition},'%') )


        and st.GRADE_ =#{grade,jdbcType=INTEGER}


        and st.TRAINING_LEVEL_ =#{trainingLevel,jdbcType=VARCHAR}


        and st.FORM_LEARNING_ =#{formLearning,jdbcType=VARCHAR}


        and st.TRAINING_CATEGORY_ =#{trainingCategory,jdbcType=VARCHAR}


        and st.FACULTY_ =#{faculty,jdbcType=VARCHAR}


        and st.PROFESSION_ =#{profession,jdbcType=VARCHAR}


        and st.CAMPUS_ =#{campus,jdbcType=VARCHAR}


        and st.IS_OVERSEAS_ =#{isOverseas,jdbcType=VARCHAR}


        and st.ENROL_SEASON_ =#{enrolSeason,jdbcType=VARCHAR}


        and st.REGISTRATION_STATUS_ =#{registrationStatus,jdbcType=VARCHAR}



GROUP BY STUDENT_ID_,CALENDAR_ID_ order by el.ID_

SELECT el.CALENDAR_ID_ calendarId,el.TEACHING_CLASS_ID_ teachingClassId,cl.CODE_ classCode,co.CODE_ courseCode,co.NAME_ courseName,co.NATURE_ nature,co.COLLEGE_ faculty,tch.TEACHER_CODE_ teacherCode,tch.TEACHER_NAME_ teacherName,tch.SEX_ sex FROM `elc_course_takes_t` el LEFT JOIN course_t co on co.CODE_=el.COURSE_CODE_ LEFT JOIN teaching_class_t cl on el.TEACHING_CLASS_ID_=cl.ID_ LEFT JOIN (select ti.TEACHING_CLASS_ID_,ro.TEACHER_CODE_ ,ch.TEACHER_NAME_,ch.SEX_ from teaching_class_arrange_time_t ti LEFT JOIN teaching_class_arrange_room_t ro on ro.ARRANGE_TIME_ID_=ti.ID_ LEFT JOIN (select TEACHER_CODE_,TEACHER_NAME_,SEX_ from teaching_class_teacher_t GROUP BY TEACHER_CODE_) ch on ro.TEACHER_CODE_=ch.TEACHER_CODE_ GROUP BY ti.TEACHING_CLASS_ID_,ro.TEACHER_CODE_) tch on tch.TEACHING_CLASS_ID_=el.TEACHING_CLASS_ID_
and el.CALENDAR_ID_ = #{calendarId,jdbcType=BIGINT}
and co.COLLEGE_ =#{faculty,jdbcType=VARCHAR}
and co.NATURE_ =#{nature,jdbcType=VARCHAR}
and (tch.TEACHER_CODE_ like concat('%',#{condition},'%') or tch.TEACHER_NAME_ like concat('%',#{condition},'%') )
GROUP BY el.CALENDAR_ID_,el.TEACHING_CLASS_ID_,tch.TEACHER_CODE_

SELECT st.STUDENT_CODE_ studentCode, st.NAME_ NAME, st.TRAINING_LEVEL_ trainingLevel, st.FACULTY_ faculty, st.PROFESSION_ profession FROM student_t st JOIN election_rounds_stu_t t2 ON ( st.STUDENT_CODE_ = t2.STUDENT_ID_ AND t2.ROUNDS_ID_ IN ( SELECT ID_ FROM `election_rounds_t` WHERE CALENDAR_ID_=#{calendarId} AND MODE_=#{mode} ) )
and (st.STUDENT_CODE_ like concat ('%',#{stuName},'%') or st.NAME_ like concat ('%',#{stuName},'%'))
and st.FACULTY_ =#{faculty}
and st.PROFESSION_ =#{profession}

SELECT t1.ID_ id,t1.CALENDAR_ID_ calendarId,t1.TEACHING_CLASS_ID_ teachingClassId,t2.CODE_ classCode,t2.NAME_ className,t3.CODE_ courseCode,t3.NAME_ courseName,t2.ELC_NUMBER_ selectCourseNumber,t2.NUMBER_ numberLimit,t3.LABEL_ courseLabel,t3.COLLEGE_ faculty
from elc_course_takes_t t1
join teaching_class_t t2 on t2.ID_=t1.TEACHING_CLASS_ID_
join course_t t3 on t1.COURSE_CODE_=t3.CODE_


        and t1.CALENDAR_ID_ =#{calendarId,jdbcType=BIGINT}


        and t3.COLLEGE_ =#{faculty,jdbcType=VARCHAR}


        and t3.CODE_  like concat('%',#{courseCode,jdbcType=VARCHAR},'%')


        and t3.NAME_ like concat ('%',#{courseName,jdbcType=VARCHAR},'%')


        and t3.LABEL_ =#{courseLabel,jdbcType=VARCHAR}


GROUP BY t1.TEACHING_CLASS_ID_,t1.CALENDAR_ID_

select TEACHING_CLASS_ID_ teachingClassId,TEACHER_NAME_ teacherName from teaching_class_teacher_t

    TEACHING_CLASS_ID_ in

        #{id}

SELECT t1.ID_ id,t1.CALENDAR_ID_ calendarId,t1.TEACHING_CLASS_ID_ teachingClassId,t2.CODE_ classCode,t2.NAME_ className,t3.CODE_ courseCode,t3.NAME_ courseName,t2.ELC_NUMBER_ selectCourseNumber,t2.NUMBER_ numberLimit,t3.LABEL_ courseLabel,t3.COLLEGE_ faculty from elc_course_takes_t t1 join teaching_class_t t2 on t2.ID_=t1.TEACHING_CLASS_ID_ join course_t t3 on t1.COURSE_CODE_=t3.CODE_
and t1.TEACHING_CLASS_ID_ in (select TEACHING_CLASS_ID_ from teaching_class_teacher_t where TEACHER_CODE_=#{teacherCode})
and t1.CALENDAR_ID_ =#{calendarId,jdbcType=BIGINT}
and t3.COLLEGE_ =#{faculty,jdbcType=VARCHAR}
and t3.CODE_ like concat('%',#{courseCode,jdbcType=VARCHAR},'%')
and t3.NAME_ like concat ('%',#{courseName,jdbcType=VARCHAR},'%')
and t3.LABEL_ =#{courseLabel,jdbcType=VARCHAR}
GROUP BY t1.TEACHING_CLASS_ID_,t1.CALENDAR_ID_

SELECT t1.CALENDAR_ID_ calendarId, t2.TEACHER_CODE_ teacherCode, t2.TEACHER_NAME_ teacherName, t3.CODE_ classCode, t1.COURSE_CODE_ courseCode, t4.NAME_ courseName, t4.COLLEGE_ faculty, t2.SEX_ sex, t1.TEACHING_CLASS_ID_ teachingClassId FROM (select TEACHING_CLASS_ID_,COURSE_CODE_,CALENDAR_ID_ from `elc_course_takes_t` where CALENDAR_ID_=#{calendarId,jdbcType=BIGINT} GROUP BY TEACHING_CLASS_ID_) t1 JOIN teaching_class_teacher_t t2 ON t1.TEACHING_CLASS_ID_ = t2.TEACHING_CLASS_ID_ JOIN teaching_class_t t3 ON t1.TEACHING_CLASS_ID_ = t3.ID_ JOIN course_t t4 ON t4.CODE_ = t1.COURSE_CODE_
and t4.COLLEGE_ =#{faculty,jdbcType=VARCHAR}
and t4.NATURE_ =#{nature,jdbcType=VARCHAR}
and (t2.TEACHER_CODE_ like concat('%',#{condition,jdbcType=VARCHAR},'%') or t2.TEACHER_NAME_ like concat('%',#{condition,jdbcType=VARCHAR},'%'))
