#{id}
GROUP BY ro.ARRANGE_TIME_ID_,ti.TIME_START_,ti.TIME_END_,ro.ROOM_ID_ ,ro.TEACHER_CODE_ order by ti.DAY_OF_WEEK_,ti.TIME_START_

