and a.PROJ_ID_ = #{projectId}
and a.CALENDAR_ID_ = #{calendarId,jdbcType=BIGINT}
and a.FACULTY_ =#{faculty,jdbcType=VARCHAR}
and a.COURSE_CODE_ like concat('%',#{courseCode,jdbcType=VARCHAR},'%')
and a.COURSE_NAME_ like concat ('%',#{courseName,jdbcType=VARCHAR},'%')
and a.NATURE_ =#{courseNature,jdbcType=VARCHAR}
and c.MAN_ARRANGE_FLAG_ in (1,2,3)
and c.ID_ in (select TEACHING_CLASS_ID_ from teaching_class_teacher_t where TEACHER_CODE_= #{teacherCode})
and c.CODE_ =#{classCode,jdbcType=VARCHAR}

SELECT t1.CALENDAR_ID_ calendarId, t2.TEACHER_CODE_ teacherCode, t2.TEACHER_NAME_ teacherName, t3.CODE_ classCode, t1.COURSE_CODE_ courseCode, t1.COURSE_NAME_ courseName, t1.FACULTY_ faculty, t2.SEX_ sex, t3.ID_ teachingClassId FROM teaching_class_t t3 join teaching_task_t t4 on t3.TASK_ID_=t4.ID_ join course_open_t t1 on t4.COURSE_OPEN_ID_=t1.ID_ JOIN teaching_class_teacher_t t2 ON t3.ID_ = t2.TEACHING_CLASS_ID_
t3.DEPLOY_FLAG_ > 0
and t1.CALENDAR_ID_ =#{calendarId,jdbcType=BIGINT}
and t1.FACULTY_ =#{faculty,jdbcType=VARCHAR}
and t1.NATURE_ =#{nature,jdbcType=VARCHAR}
and (t2.TEACHER_CODE_ like concat('%',#{keyWord,jdbcType=VARCHAR},'%') or t2.TEACHER_NAME_ like concat('%',#{keyWord,jdbcType=VARCHAR},'%'))

select distinct tt.TEACHER_CODE_ teacherCode, tt.TEACHER_NAME_ teacherName, op.CALENDAR_ID_ calendarId, op.IS_ELECTIVE_ isElective, tc.CODE_ classCode, tc.NAME_ className, tc.ELC_NUMBER_ elcNumber, op.COURSE_CODE_ courseCode, op.COURSE_NAME_ courseName, op.NATURE_ nature, op.FACULTY_ faculty, tc.ID_ teachingClassId from course_open_t op join teaching_task_t tk on op.ID_ = tk.COURSE_OPEN_ID_ join teaching_class_t tc on tk.ID_ = tc.TASK_ID_ left join teaching_class_arrange_time_t ta on (tc.ID_ = ta.TEACHING_CLASS_ID_ and ta.TYPE_ = 2) join teaching_class_teacher_t tt on tc.ID_ = tt.TEACHING_CLASS_ID_
op.CALENDAR_ID_ = #{calendarId,jdbcType=BIGINT} and op.PROJ_ID_ = #{projectId}
and op.FACULTY_ =#{faculty,jdbcType=VARCHAR}
and op.NATURE_ =#{nature,jdbcType=VARCHAR}
and op.IS_ELECTIVE_ =#{isElective,jdbcType=VARCHAR}
and op.COURSE_CODE_ like CONCAT( '%', #{courseCode}, '%')
and op.COURSE_NAME_ like CONCAT( '%', #{courseName}, '%')
and (tt.TEACHER_CODE_ like CONCAT( '%', #{keyWord}, '%') or tt.TEACHER_NAME_ like CONCAT( '%', #{keyWord}, '%'))
and tc.CODE_ like CONCAT( '%', #{classCode}, '%')
group by tc.ID_

select
