#{item}
group by t2.ARRANGE_TIME_ID_,t1.TIME_START_,t1.TIME_END_ order by t1.DAY_OF_WEEK_,t1.TIME_START_

select t0.ID_ teachingClassId,t1.DAY_OF_WEEK_ dayOfWeek, t1.TIME_START_ timeStart, t1.TIME_END_ timeEnd, t2.ROOM_ID_ roomId,GROUP_CONCAT(t2.WEEK_NUMBER_ ORDER BY t2.WEEK_NUMBER_ asc) weekNum from teaching_class_t t0 join teaching_class_arrange_time_t t1 on t1.TEACHING_CLASS_ID_ = t0.ID_ join teaching_class_arrange_room_t t2 on t2.ARRANGE_TIME_ID_ = t1.ID_ where t0.ID_ in
