SELECT t1.ID_, t1.STUDENT_ID_, t1.`CALENDAR_ID_`, t1.`COURSE_CODE_`, t1.`PAID_`, t1.`MODE_`, t1.`BILL_ID_`, t2.BILL_NUM_, if(t1.PAID_ = 1,rc.UNIT_PRICE_*t7.CREDITS_,0) PAY_, -- t2.`PAY_`, t2.`CREATED_AT_` payTime, t3.`NAME_` studentName, t3.`FACULTY_`, t3.`PROFESSION_`, t5.`CODE_` teachingClassCode, t7.`COURSE_NAME_` courseName, t7.`CREDITS_`, t7.`CALENDAR_ID_` couCalendarId, t7.IS_ELECTIVE_ nature, -- if(t2.ID_ is null, -- (if(rc.IS_CHARGE_ = 1,rc.UNIT_PRICE_*t7.CREDITS_,0)), -- t2.AMOUNT_) AMOUNT_ if(rc.IS_CHARGE_ = 1,rc.UNIT_PRICE_*t7.CREDITS_,0) AMOUNT_ FROM elc_course_takes_t t1 left JOIN elc_bills_t t2 ON t1.BILL_ID_ = t2.ID_ JOIN student_t t3 ON t1.`STUDENT_ID_` = t3.`STUDENT_CODE_` left JOIN rebuild_course_charge_t rc on t3.TRAINING_LEVEL_ = rc.TRAINING_LEVEL_ and t3.FORM_LEARNING_ = rc.FORM_LEARNING_ JOIN `teaching_class_t` t5 ON t1.`TEACHING_CLASS_ID_` = t5.`ID_` JOIN `teaching_task_t` t6 ON t5.`TASK_ID_` = t6.`ID_` JOIN `course_open_t` t7 ON t6.`COURSE_OPEN_ID_` = t7.`ID_`
t1.`CALENDAR_ID_` = #{calendarId} AND t1.`COURSE_TAKE_TYPE_` = 2
and t5.CODE_ = #{teachingClassCode}
and t7.COURSE_NAME_ = #{courseName}
and t3.FACULTY_ = #{faculty}
and t3.PROFESSION_ = #{profession}
and t7.IS_ELECTIVE_ = #{nature}
and t1.PAID_ = #{paid}
and t7.CALENDAR_ID_ = #{couCalendarId}
and (t1.STUDENT_ID_ like concat('%',#{keyword},'%') OR t3.`NAME_` like concat('%',#{keyword},'%'))
and t1.ID_ in
#{item}
and t3.MANAGER_DEPT_ID_ = #{manageDptId}
and st.STUDENT_CODE_ =#{studentId,jdbcType=VARCHAR}

SELECT st.STUDENT_CODE_ studentCode, st.NAME_ studentName, st.TRAINING_LEVEL_ trainingLevel, te.CODE_ teachingClassCode, ct.NAME_ courseName, ct.IS_ELECTIVE_ nature, CONCAT(te.START_WEEK_,'-',te.END_WEEK_,'周',te.PERIOD_,'学时') courseArr, ct.CREDITS_ credits, el.PAID_ paid, CASE WHEN el.ID_ is null THEN 0 ELSE 1 END as chooseObj, vp.planCourseId planCourseId FROM student_t st JOIN v_student_plan_course vp on st.STUDENT_CODE_ = vp.studentId and vp.score is not null and vp.isPass = 0 JOIN course_t ct on vp.courseCode = ct.CODE_ left JOIN elc_course_takes_t el on vp.courseCode = el.COURSE_CODE_ and el.CALENDAR_ID_ = #{calendarId} and el.COURSE_TAKE_TYPE_ =2 and el.STUDENT_ID_ = #{studentId} left JOIN teaching_class_t te on el.TEACHING_CLASS_ID_=te.ID_ WHERE st.STUDENT_CODE_ = #{studentId}
and vp.planCourseId in
#{planCourseId}

