   and t2.FORM_LEARNING_= #{elcResult.formLearning} 


    and t2.FACULTY_ = #{query.faculty}


	 and t3.CODE_ = #{query.courseCode}


    and t3.COLLEGE_ = #{courseFaculty}


	  and t3.NAME_ = #{query.courseName}


