SELECT t1.CALENDAR_ID_ calendarId, t2.TEACHER_CODE_ teacherCode, t2.TEACHER_NAME_ teacherName, t3.CODE_ classCode, t1.COURSE_CODE_ courseCode, t1.COURSE_NAME_ courseName, t1.FACULTY_ faculty, t2.SEX_ sex, t3.ID_ teachingClassId FROM teaching_class_t t3 join teaching_task_t t4 on t3.TASK_ID_=t4.ID_ join course_open_t t1 on t4.COURSE_OPEN_ID_=t1.ID_ JOIN teaching_class_teacher_t t2 ON t3.ID_ = t2.TEACHING_CLASS_ID_
t3.DEPLOY_FLAG_ > 0
and t1.CALENDAR_ID_ =#{calendarId,jdbcType=BIGINT}
and t1.FACULTY_ =#{faculty,jdbcType=VARCHAR}
and t1.NATURE_ =#{nature,jdbcType=VARCHAR}
