select
op.CALENDAR_ID_ calendarId,
tr.TEACHER_CODE_ teacherCode,
tc.CODE_ classCode,
tc.ELC_NUMBER_ elcNumber,
op.COURSE_CODE_ courseCode,
op.COURSE_NAME_ courseName,
op.FACULTY_ faculty,
op.NATURE_ nature,
ta.ID_,
tc.ID_ teachingClassId
from course_open_t op
join teaching_task_t tk ON tk.COURSE_OPEN_ID_ = op.ID_
join teaching_class_t tc ON tc.TASK_ID_ = tk.ID_
join teaching_class_arrange_time_t ta ON ta.TEACHING_CLASS_ID_ = tc.ID_
join teaching_class_arrange_room_t tr ON tr.ARRANGE_TIME_ID_ = ta.ID_

    ta.TYPE_ = 2 and op.CALENDAR_ID_ = #{calendarId,jdbcType=BIGINT}

        and op.FACULTY_ =#{faculty,jdbcType=VARCHAR}


        and op.NATURE_ =#{nature,jdbcType=VARCHAR}


        and op.COURSE_CODE_ =#{courseCode,jdbcType=VARCHAR}


        and op.COURSE_NAME_ =#{courseName,jdbcType=VARCHAR}


        and te.TEACHER_CODE_ =#{teacherCode,jdbcType=VARCHAR}


        and tc.CODE_ =#{classCode,jdbcType=VARCHAR}


        and tr.TEACHER_CODE_ in

            #{item}



group by tc.ID_,tr.TEACHER_CODE_

