mapper.select:
SELECT t1.ID_ id, t1.`CALENDAR_ID_` calendarId, t1.`STUDENT_ID_` studentId, t2.`NAME_` studentName, t2.`SEX_` sex, t2.`TRAINING_LEVEL_` trainingLevel, t2.`FACULTY_` faculty, t2.`PROFESSION_` profession, t2.`GRADE_` grade, t2.`MANAGER_DEPT_ID_` projectId, t2.TRAINING_CATEGORY_ trainingCategory, t2.DEGREE_CATEGORY_ degreeCategory, t2.FORM_LEARNING_ formLearning , t2.DEGREE_TYPE_ degreeType FROM `elc_cross_stds_t` t1 JOIN `student_t` t2 ON t1.`STUDENT_ID_` = t2.`STUDENT_CODE_`
t1.CALENDAR_ID_ = #{calendarId}
and t1.STUDENT_ID_ like concat ('%', #{studentId},'%')
and t2.MANAGER_DEPT_ID_ = #{projectId}
and t2.NAME_ like concat ('%', #{studentName},'%')
and (t1.STUDENT_ID_ like concat ('%', #{keyWords},'%') or t2.NAME_ like concat ('%', #{keyWords},'%'))
and t2.GRADE_ = #{grade}
and t2.FACULTY_ in
'${item}'
and t2.PROFESSION_ = #{profession}
and t2.TRAINING_LEVEL_ = #{trainingLevel}

SELECT ID_ id, CALENDAR_ID_ calendarId, STUDENT_ID_ studentId FROM elc_cross_stds_t
and CALENDAR_ID_ = #{calendarId}
and STUDENT_ID_ = #{studentId}

mapper.delete:
DELETE FROM elc_cross_stds_t
WHERE 1=1


	AND CALENDAR_ID_=#{calendarId}



	AND STUDENT_ID_ IN

		#{item}

mapper.select:
SELECT distinct(t1.STUDENT_ID_) FROM elc_cross_stds_t t1 JOIN student_t st ON t1.`STUDENT_ID_` = st.`STUDENT_CODE_` WHERE 1 = 1
AND st.FACULTY_ IN
#{facultyCondition}
