mapper.select:
SELECT t1.ID_ id, t1.STUDENT_ID_ studentId, t1.WITHDRAW_FLAG_ withdrawFlag, t2.ID_ teachingClassId, t2.CODE_ CODE, t2.NAME_ NAME, t2.CAMPUS_ campus, t4.COURSE_CODE_ courseCode, t4.COURSE_NAME_ courseName, t4.COURSE_NAME_EN_ courseNameEn, t4.TRAINING_LEVEL_ trainingLevel, t4.NATURE_ nature, t4.FACULTY_ faculty, t5.OPRATION_OBJ_CODE, t5.OPRATION_OBJ_NAME, t6.NAME_ studentName FROM elc_med_withdraw_apply_t t1 INNER JOIN teaching_class_t t2 ON t1.TEACHING_CLASS_ID_ = t2.ID_ INNER JOIN teaching_task_t t3 ON t2.TASK_ID_ = t3.ID_ INNER JOIN course_open_t t4 ON t4.ID_ = t3.COURSE_OPEN_ID_ LEFT JOIN elc_med_withdraw_apply_log_t t5 ON
(t5.TEACHING_CLASS_ID_ = t1.TEACHING_CLASS_ID_ and t5.OPRATION_OBJ_CODE=t1.STUDENT_ID_)
(t5.TEACHING_CLASS_ID_ = t1.TEACHING_CLASS_ID_ and t5.OPRATION_OBJ_CODE!=t1.STUDENT_ID_)
INNER JOIN student_t t6 ON t1.STUDENT_ID_ = t6.STUDENT_CODE_
1=1
and t4.CALENDAR_ID_= #{calendarId}
and t4.CAMPUS_= #{campus}
and t4.FACULTY_= #{faculty}
and t4.NATURE_= #{nature}
and t4.COURSE_CODE_ like concat(#{courseCode},'%')
and (t4.COURSE_CODE_ like concat(#{keyWord},'%') or t2.CODE_ like concat(#{keyWord},'%'))
and t1.WITHDRAW_FLAG_ = #{withdrawFlag}
and t1.STUDENT_ID_ like concat(#{studentId},'%')

SELECT t1.ID_ id, t1.STUDENT_ID_ studentId, t1.WITHDRAW_FLAG_ withdrawFlag, t2.CONTENT_ content, t2.OPRATION_OBJ_CODE oprationObjCode, t2.OPRATION_OBJ_NAME oprationObjName, t3.ID_ teachingClassId, t3.CODE_ CODE, t3.NAME_ NAME, t3.CAMPUS_ campus, t3.PERIOD_ period, t5.COURSE_CODE_ courseCode, t5.COURSE_NAME_ courseName, t5.COURSE_NAME_EN_ courseNameEn, t5.TRAINING_LEVEL_ trainingLevel, t5.NATURE_ nature, t5.FACULTY_ faculty, t5.CREDITS_ credits FROM elc_med_withdraw_apply_t t1 INNER JOIN elc_med_withdraw_apply_log_t t2 ON ( t1.STUDENT_ID_ = t2.STUDENT_ID_ AND t1.TEACHING_CLASS_ID_ = t2.TEACHING_CLASS_ID_ ) INNER JOIN teaching_class_t t3 ON t3.ID_ = t1.TEACHING_CLASS_ID_ INNER JOIN teaching_task_t t4 ON t3.TASK_ID_ = t4.ID_ INNER JOIN course_open_t t5 ON t5.ID_ = t4.COURSE_OPEN_ID_
and t1.STUDENT_ID_ = #{studentId}
and t5.CALENDAR_ID_= #{calendarId}
