		SELECT 
		  t1.*,
		  t4.`COURSE_CODE_`,
		  t4.`COURSE_NAME_`,
		  t4.`FACULTY_` openCollege,
		  t4.`NATURE_`,
		  t4.`IS_ELECTIVE_`,
		  t3.`ASSESSMENT_MODE_`,
		  t4.`CREDITS_`,
		  t4.`TRAINING_LEVEL_` courseTrainingLevel
		FROM
		  `elc_mutual_apply_t` t1 
		  JOIN `elc_mutual_courses_t` t2 ON t1.`MUTUAL_COURSE_ID_` = t2.`COURSE_ID_`
		  JOIN course_open_t t4 on t2.`COURSE_ID_` = t4.`ID_`
		  JOIN `course_t` t3 ON t3.CODE_ = t4.COURSE_CODE_

		    t1.CALENDAR_ID_  = #{calendarId}

		        and t1.STUDENT_ID_  = #{studentId}


		        and t2.BY_TYPE_  = #{byType}


		        and t2.IN_TYPE_  = #{inType}


		        and t4.COURSE_CODE_ like concat ('%', #{courseCode},'%')


		        and t4.COURSE_NAME_ like concat ('%', #{courseName},'%')


		        and t4.TRAINING_LEVEL_  = #{trainingLevel}


		        and t4.FACULTY_  = #{openCollege}


		        and t4.NATURE_  = #{nature}


		        and t4.IS_ELECTIVE_  = #{isElective}


		        and t4.PROJ_ID_ in  

                    #{projectId}



		        and t1.STATUS_  = #{status}

SELECT t1.`ID_`, t1.`COURSE_ID_`, t2.`COURSE_CODE_`, t2.`COURSE_NAME_`, t2.`FACULTY_` openCollege, t2.`NATURE_`, t2.`IS_ELECTIVE_`, t4.`ASSESSMENT_MODE_`, t2.`CREDITS_`, t2.`TRAINING_LEVEL_` courseTrainingLevel FROM elc_mutual_courses_t t1 JOIN course_open_t t2 ON t1.`COURSE_ID_` = t2.`ID_` left join course_t t4 on t2.COURSE_CODE_ = t4.CODE_
t1.COURSE_ID_ NOT IN (SELECT t3.MUTUAL_COURSE_ID_ FROM elc_mutual_apply_t t3 WHERE t3.`CALENDAR_ID_` = #{calendarId} AND t3.`STUDENT_ID_` = #{studentId} )
and t1.BY_TYPE_ = #{byType}
and t1.IN_TYPE_ = #{inType}
and t2.COURSE_CODE_ like concat ('%', #{courseCode},'%')
and t2.COURSE_NAME_ like concat ('%', #{courseName},'%')
and (t2.COURSE_CODE_ like concat ('%', #{keyWords},'%') or t2.COURSE_NAME_ like concat ('%', #{keyWords},'%'))
and t2.TRAINING_LEVEL_ = #{trainingLevel}
and t2.FACULTY_ = #{openCollege}
and t2.NATURE_ = #{nature}
and t2.IS_ELECTIVE_ = #{isElective}
and t2.PROJ_ID_ in
#{projectId}

SELECT t1.*, t2.COURSE_ID_, t5.`COURSE_CODE_`, t5.`COURSE_NAME_`, t5.`FACULTY_` openCollege, t5.`NATURE_`, t5.`IS_ELECTIVE_`, t3.`ASSESSMENT_MODE_`, t5.`CREDITS_`, t4.`TRAINING_LEVEL_` stuTrainingLevel, t4.`NAME_` studentName, t1.STUDENT_ID_ studentId, t4.GRADE_, t4.PROFESSION_ FROM `elc_mutual_apply_t` t1 JOIN `elc_mutual_courses_t` t2 ON t1.`MUTUAL_COURSE_ID_` = t2.`COURSE_ID_` JOIN course_open_t t5 on t5.ID_ = t2.COURSE_ID_ JOIN `course_t` t3 ON t3.`CODE_` = t5.`COURSE_CODE_` JOIN student_t t4 ON t1.`STUDENT_ID_` = t4.`STUDENT_CODE_`
t1.CALENDAR_ID_ = #{calendarId} and t4.FACULTY_ = #{college}
and t1.STUDENT_ID_ like concat ('%', #{studentId},'%')
and t4.NAME_ like concat ('%', #{studentName},'%')
and t2.BY_TYPE_ = #{byType}
and t2.IN_TYPE_ = #{inType}
and t5.COURSE_CODE_ like concat ('%', #{courseCode},'%')
and t5.COURSE_NAME_ like concat ('%', #{courseName},'%')
and t5.FACULTY_ = #{openCollege}
and t5.PROJ_ID_ in
#{projectId}
and t1.STATUS_ = #{status}
and t4.GRADE_ = #{grade}
and t4.PROFESSION_ = #{profession}
and t4.TRAINING_LEVEL_ = #{stuTrainingLevel}

		SELECT 
		  t1.`ID_`,
		  t1.`STUDENT_ID_`,
          t4.`NAME_` studentName,
          t4.`GRADE_`,
          t4.`FACULTY_`,
          t4.`PROFESSION_`,
          t4.`TRAINING_LEVEL_` stuTrainingLevel
		FROM
		  `elc_mutual_apply_t` t1 
		  JOIN `elc_mutual_courses_t` t2 ON t1.`MUTUAL_COURSE_ID_` = t2.`COURSE_ID_`
		  JOIN course_open_t t5 on t5.ID_ = t2.COURSE_ID_  
		  JOIN `course_t` t3 ON t3.`CODE_` = t5.`COURSE_CODE_`
		  JOIN `student_t` t4 ON t4.`STUDENT_CODE_` = t1.`STUDENT_ID_`

		    t1.CALENDAR_ID_  = #{calendarId}

		        and t1.STUDENT_ID_ like concat ('%', #{studentId},'%')


		        and t4.NAME_ like concat ('%', #{studentName},'%')


		        and t2.BY_TYPE_  = #{byType}


		        and t2.IN_TYPE_  = #{inType}


		        and t5.PROJ_ID_ in  

                    #{projectId}



		        and t4.GRADE_  = #{grade}


		        and t4.FACULTY_  = #{college}


		        and t4.PROFESSION_  = #{profession}


		        and t4.TRAINING_LEVEL_  = #{stuTrainingLevel}


		GROUP BY t1.`STUDENT_ID_`

		SELECT 
		  t1.`ID_`,
		  t1.`MUTUAL_COURSE_ID_`,
		  t1.STATUS_,
		  t1.REMARK_,
		  t1.AUDIT_REASON_,
		  t2.`COURSE_ID_`,
		  t4.`COURSE_CODE_`,
		  t4.`COURSE_NAME_`,
		  t4.`NATURE_`,
		  t4.IS_ELECTIVE_,
		  t4.`CREDITS_`,
		  t4.`TRAINING_LEVEL_` courseTrainingLevel,
		  count(t1.STUDENT_ID_) stuNumber
		FROM
		  elc_mutual_apply_t t1 
		  JOIN elc_mutual_courses_t t2 ON t1.MUTUAL_COURSE_ID_ = t2.COURSE_ID_
		  JOIN course_open_t t4 ON t4.ID_ = t2.COURSE_ID_ 
		  JOIN course_t t3 ON t3.CODE_ = t4.COURSE_CODE_

		    t1.CALENDAR_ID_  = #{calendarId}
		    and t4.FACULTY_ = #{openCollege}		    

		        and t2.BY_TYPE_  = #{byType}


		        and t2.IN_TYPE_  = #{inType}


		        and t4.COURSE_CODE_ like concat ('%', #{courseCode},'%')


		        and t4.COURSE_NAME_ like concat ('%', #{courseName},'%')


		        and t4.TRAINING_LEVEL_  = #{trainingLevel}


		        and t4.NATURE_  = #{nature}


		        and t4.IS_ELECTIVE_  = #{isElective}


		        and t1.STATUS_ = #{status} 


		        and t1.STATUS_ in  

                     #{status}



		        and t4.PROJ_ID_ = #{projectId}


		 GROUP BY t4.COURSE_CODE_

SELECT t1.`ID_`, t5.COURSE_CODE_, t5.COURSE_NAME_, t1.`STUDENT_ID_`, t4.`NAME_` studentName, t4.`GRADE_`, t4.`FACULTY_` college, t4.`PROFESSION_`, t1.REMARK_, t1.STATUS_, t4.STUDENT_CATEGORY_, t4.TRAINING_LEVEL_ stuTrainingLevel FROM `elc_mutual_apply_t` t1 JOIN `elc_mutual_courses_t` t2 ON t1.`MUTUAL_COURSE_ID_` = t2.`COURSE_ID_` JOIN course_open_t t5 on t5.ID_ = t2.COURSE_ID_ JOIN `course_t` t3 ON t3.`CODE_` = t5.`COURSE_CODE_` JOIN `student_t` t4 ON t4.`STUDENT_CODE_` = t1.`STUDENT_ID_`
t1.CALENDAR_ID_ = #{calendarId} and t5.FACULTY_ = #{openCollege}
and t1.STUDENT_ID_ like concat ('%', #{studentId},'%')
and t4.NAME_ like concat ('%', #{studentName},'%')
and t5.COURSE_CODE_ like concat ('%', #{courseCode},'%')
and t5.COURSE_NAME_ like concat ('%', #{courseName},'%')
and t2.BY_TYPE_ = #{byType}
and t2.IN_TYPE_ = #{inType}
and t4.GRADE_ = #{grade}
and t4.FACULTY_ = #{college}
and t4.PROFESSION_ = #{profession}
and t5.PROJ_ID_ = #{projectId}
and t1.STATUS_ in
#{status}
and t1.STATUS_ = #{status}

SELECT t1.`ID_`, t1.CALENDAR_ID_, t1.`MUTUAL_COURSE_ID_`, t1.STATUS_, t4.`COURSE_CODE_`, t4.`COURSE_NAME_`, t4.CREDITS_, t4.FACULTY_, t6.CODE_ teachingClassCode, t6.ID_ teachingClassId, t4.NATURE_, t4.IS_ELECTIVE_, t6.CAMPUS_, t4.TERM_, t6.ASSESSMENT_MODE_ FROM elc_mutual_apply_t t1 JOIN elc_mutual_courses_t t2 ON t1.MUTUAL_COURSE_ID_ = t2.COURSE_ID_ JOIN course_open_t t4 ON t4.ID_ = t2.COURSE_ID_ JOIN course_t t3 ON t3.CODE_ = t4.COURSE_CODE_ left join teaching_task_t t5 on t4.ID_ = T5.COURSE_OPEN_ID_ left join teaching_class_t t6 on t5.ID_ = t6.TASK_ID_
t1.CALENDAR_ID_ = #{calendarId} and t1.STUDENT_ID_ = #{studentId} and t1.STATUS_ = #{status}
and t4.PROJ_ID_ in
#{projectId}
and t2.BY_TYPE_ = #{byType}
and t2.IN_TYPE_ = #{inType}
GROUP BY t4.COURSE_CODE_
