mapper.select:
SELECT id, studentId, calendarId, projectId, newLimitCredits, totalLimitCredits, rebuildLimitNumber, NAME, sex, grade, faculty, profession, trainingLevel, studentCategory, researchDirection, IFNULL(selectedCredits,0) selectedCredits, IFNULL(selectedRebuild,0) selectedRebuild from( SELECT DISTINCT t1.ID_ id, t1.STUDENT_ID_ studentId, t1.CALENDAR_ID_ calendarId, t1.PROJECT_ID_ projectId, t1.NEW_LIMIT_CREDITS_ newLimitCredits, t1.TOTAL_LIMIT_CREDITS_ totalLimitCredits, t1.REBUILD_LIMIT_NUMBER_ rebuildLimitNumber, t4.NAME_ name, t4.SEX_ sex, t4.GRADE_ grade, t4.FACULTY_ faculty, t4.PROFESSION_ profession, t4.TRAINING_LEVEL_ trainingLevel, t4.STUDENT_CATEGORY_ studentCategory, t4.RESEARCH_DIRECTION_ researchDirection, CASE WHEN t2.COURSE_TAKE_TYPE_ = 1 THEN sum(t3.CREDITS_) END selectedCredits, CASE WHEN t2.COURSE_TAKE_TYPE_ = 2 THEN sum(t3.CREDITS_) END selectedRebuild FROM elc_student_limit_t t1 LEFT JOIN elc_course_takes_t_${index} t2 ON ( t1.STUDENT_ID_ = t2.STUDENT_ID_ AND t1.CALENDAR_ID_ = t2.CALENDAR_ID_ AND t2.CALENDAR_ID_ = #{calendarId} ) LEFT JOIN course_t t3 ON t3.CODE_ = t2.COURSE_CODE_ JOIN student_t t4 ON t1.STUDENT_ID_ = t4.STUDENT_CODE_
and t1.ID_ = #{id}
and t1.CALENDAR_ID_ = #{calendarId}
and t1.STUDENT_ID_ = #{studentId}
and t4.NAME_ = #{name}
and t4.PROFESSION_ = #{profession}
and t4.FACULTY_ = #{faculty}
and t4.GRADE_ = #{grade}
and t4.MANAGER_DEPT_ID_ = #{projectId}
and t1.NEW_LIMIT_CREDITS_ >= #{minLimitCredits} and t1.NEW_LIMIT_CREDITS_ <= #{maxLimitCredits}
and t1.REBUILD_LIMIT_NUMBER_ >= #{minRebuild} and t1.REBUILD_LIMIT_NUMBER_ <= #{maxRebuild}
GROUP BY t1.STUDENT_ID_ ) t5
and t5.selectedCredits >= #{minSelectedCredits} and t5.selectedCredits <= #{maxSelectedCredits}
and t5.selectedRebuild >= #{minSelectedRebuild} and t5.selectedRebuild <= #{maxSelectedRebuild}
