mapper.select:
SELECT t1.CALENDAR_ID_, t1.COURSE_CODE_, t1.COURSE_NAME_, t1.NATURE_, t1.FACULTY_, t1.CREDITS_, t1.PERIOD_, t3.ID_ teachingClassId, t3.CODE_ teachingClassCode, t3.COURSE_LABEL_ID_, GROUP_CONCAT(DISTINCT t5.TEACHER_CODE_) teacherCode FROM course_open_t t1 JOIN teaching_task_t t2 ON t2.COURSE_OPEN_ID_ = t1.ID_ JOIN teaching_class_t t3 ON t3.TASK_ID_ = t2.ID_ JOIN election_rounds_cour_t t4 ON t4.TEACHING_CLASS_ID_ = t3.ID_ LEFT JOIN teaching_class_teacher_t t5 ON ( t5.TEACHING_CLASS_ID_ = t3.ID_ AND t5.TYPE_ = 0 )
t1.CALENDAR_ID_ = #{query.calendarId}
and t4.ROUNDS_ID_ = #{query.roundId}
and t1.PROJ_ID_ = #{query.projectId}
and t1.FACULTY_ = #{query.faculty}
and t1.TRAINING_LEVEL_ = #{query.trainingLevel}
and t1.COURSE_CODE_ = #{query.courseCode}
and t1.COURSE_NAME_ = #{query.courseName}
and t3.COURSE_LABEL_ID_ = #{query.label}
and (t1.COURSE_CODE_ like CONCAT(#{query.keyword}, '%') OR t1.COURSE_NAME_ like CONCAT(#{query.keyword}, '%'))
GROUP BY t3.ID_ order by t3.ID_

SELECT DISTINCT t1.CALENDAR_ID_, t1.COURSE_CODE_, t1.COURSE_NAME_, t1.NATURE_, t1.FACULTY_, t1.CREDITS_, t1.PERIOD_, t4.ID_ teachingClassId, t4.CODE_ teachingClassCode, t4.COURSE_LABEL_ID_, GROUP_CONCAT(distinct t5.TEACHER_CODE_) teacherCode FROM course_open_t t1 JOIN teaching_task_t t3 ON t3.COURSE_OPEN_ID_ = t1.ID_ JOIN teaching_class_t t4 ON (t4.TASK_ID_ = t3.ID_ and t4.DELETE_STATUS_ =0) LEFT JOIN teaching_class_teacher_t t5 ON (t5.TEACHING_CLASS_ID_ = t4.ID_ AND t5.TYPE_ =0)
t1.CALENDAR_ID_ = #{query.calendarId} and t1.IS_OPEN_ = 1
and t1.PROJ_ID_ = #{query.projectId}
and t4.DEPLOY_FLAG_ > 0 and t4.ID_ NOT IN ( select t.TEACHING_CLASS_ID_ from election_rounds_cour_t t where t.ROUNDS_ID_ = #{query.roundId} )
and t1.COURSE_CODE_ in
#{code}
and t1.FACULTY_ = #{query.faculty}
and t1.TRAINING_LEVEL_ = #{query.trainingLevel}
and t1.COURSE_CODE_ = #{query.courseCode}
and t1.COURSE_NAME_ = #{query.courseName}
and t4.COURSE_LABEL_ID_ = #{query.label}
and (t1.COURSE_CODE_ like CONCAT(#{query.keyword}, '%') OR t1.COURSE_NAME_ like CONCAT(#{query.keyword}, '%'))
GROUP BY t4.ID_ order by t4.ID_

select t1.CALENDAR_ID_, t4.CODE_ id, t1.COURSE_CODE_, t1.COURSE_NAME_, t1.NATURE_, t4.NAME_ teachingClassName, t1.TRAINING_LEVEL_, t1.FACULTY_, t4.CAMPUS_ campus, t4.ID_ teachingClassId, t4.CODE_ teachingClassCode from course_open_t t1 JOIN teaching_task_t t3 ON t3.COURSE_OPEN_ID_ = t1.ID_ JOIN teaching_class_t t4 ON t4.TASK_ID_ = t3.ID_ JOIN election_rounds_cour_t t2 ON t2.TEACHING_CLASS_ID_ = t4.ID_
t1.CALENDAR_ID_ = #{query.calendarId}
and t2.ROUNDS_ID_ = #{query.roundId}
and t1.PROJ_ID_ = #{query.projectId}
and t1.FACULTY_ = #{query.faculty}
and t1.NATURE_ = #{query.nature}
and (t4.CODE_ like CONCAT(#{query.keyword}, '%') OR t1.COURSE_NAME_ like CONCAT(#{query.keyword}, '%'))
and t4.CAMPUS_ = #{query.campus}
order by t4.CODE_

select t1.CALENDAR_ID_, t4.CODE_ teachingClassCode, t1.COURSE_CODE_, t1.COURSE_NAME_, t1.NATURE_, t4.NAME_ teachingClassName, t1.TRAINING_LEVEL_, t1.FACULTY_, t4.CAMPUS_ campus, t4.ID_ teachingClassId, t4.CODE_ teachingClassCode from course_open_t t1 JOIN teaching_task_t t3 ON t3.COURSE_OPEN_ID_ = t1.ID_ JOIN teaching_class_t t4 ON t4.TASK_ID_ = t3.ID_
t1.CALENDAR_ID_ = #{query.calendarId} and t1.IS_OPEN_ = 1
and t1.PROJ_ID_ = #{query.projectId}
and t4.ID_ NOT IN ( select TEACHING_CLASS_ID_ from election_rounds_cour_t where ROUNDS_ID_ = #{query.roundId} )
and t1.FACULTY_ = #{query.faculty}
and t1.NATURE_ = #{query.nature}
and t4.CAMPUS_ = #{query.campus}
and (t4.CODE_ like CONCAT(#{query.keyword}, '%') OR t1.COURSE_NAME_ like CONCAT(#{query.keyword}, '%'))
order by t4.CODE_

select op.*,cl.ID_ AS teachingClassId, cl.CODE_ AS teachingClassCode from course_open_t op join teaching_task_t ta on op.ID_=ta.COURSE_OPEN_ID_ JOIN teaching_class_t cl on cl.TASK_ID_=ta.ID_
cl.ID_ in ( SELECT DISTINCT co.TEACHING_CLASS_ID_ FROM election_rounds_t el join election_rounds_cour_t co on el.ID_=co.ROUNDS_ID_ where el.CALENDAR_ID_=#{calendarId} and el.MODE_=#{mode} ) and op.IS_OPEN_=1 and op.CALENDAR_ID_=#{calendarId}
and cl.CODE_ like CONCAT(#{courseCode},'%')
and ( op.COURSE_CODE_ like CONCAT(#{courseName},'%') or op.COURSE_NAME_ like CONCAT(#{courseName},'%') )
and op.COURSE_CODE_ in
#{code}

SELECT t1.*,t4.COURSE_CODE_,t4.COURSE_NAME_ FROM election_rounds_cour_t t1 JOIN teaching_class_t t2 ON t1.TEACHING_CLASS_ID_ = t2.ID_ JOIN teaching_task_t t3 ON t3.ID_ = t2.TASK_ID_ JOIN course_open_t t4 ON t4.ID_ = t3.COURSE_OPEN_ID_
t1.ROUNDS_ID_ in
#{id}
