mapper.select:
  	select t1.* from course_open_t t1 
  	JOIN election_rounds_cour_t t2 ON t2.COURSE_CODE_ = t1.COURSE_CODE_

	  	t1.CALENDAR_ID_ = #{query.calendarId} 

  		and t2.ROUNDS_ID_ = #{query.roundId}


  		and t1.PROJ_ID_ = #{query.projectId}


  		and t1.FACULTY_ = #{query.faculty}


  		and t1.TRAINING_LEVEL_ = #{query.trainingLevel}


  		and t1.NATURE_ = #{query.nature}


  		and (t1.COURSE_CODE_ like CONCAT(#{query.keyword}, '%') OR t1.COURSE_NAME_ like CONCAT(#{query.keyword}, '%'))


  	order by t1.COURSE_CODE_

    select * from(
	  	select DISTINCT t1.* 
		from course_open_t t1 
	  	JOIN teaching_task_t t3 ON t3.COURSE_OPEN_ID_ = t1.ID_
	  	JOIN teaching_class_t t4 ON t4.TASK_ID_ = t3.ID_

		  	t1.CALENDAR_ID_ = #{query.calendarId}
		  	and t1.IS_OPEN_ = 1

		  	and t1.PROJ_ID_ = #{query.projectId}

			and t4.DEPLOY_FLAG_ =1
		  	and t1.COURSE_CODE_ NOT IN (
		  	 select COURSE_CODE_ from election_rounds_cour_t where ROUNDS_ID_ = #{query.roundId}
		  	)

				and t1.COURSE_CODE_ in 

					#{code}



	  		and t1.FACULTY_ = #{query.faculty}


	  		and t1.TRAINING_LEVEL_ = #{query.trainingLevel}


	  		and t1.NATURE_ = #{query.nature}


	  	order by t1.COURSE_CODE_
    )tt


  		 (tt.COURSE_CODE_ like CONCAT(#{query.keyword}, '%') OR tt.COURSE_NAME_ like CONCAT(#{query.keyword}, '%'))

select op.*,cl.ID_ AS teachingClassId, cl.CODE_ AS teachingClassCode from course_open_t op join teaching_task_t ta on op.ID_=ta.COURSE_OPEN_ID_ JOIN teaching_class_t cl on cl.TASK_ID_=ta.ID_
op.COURSE_CODE_ in ( SELECT DISTINCT co.COURSE_CODE_ FROM election_rounds_t el join election_rounds_cour_t co on el.ID_=co.ROUNDS_ID_ where el.CALENDAR_ID_=#{calendarId} and el.MODE_=#{mode} ) and op.IS_OPEN_=1 and op.CALENDAR_ID_=#{calendarId}
and cl.CODE_ like CONCAT(#{courseCode},'%')
and ( op.COURSE_CODE_ like CONCAT(#{courseName},'%') or op.COURSE_NAME_ like CONCAT(#{courseName},'%') )
and op.COURSE_CODE_ in
#{code}
