SELECT
  t1.ID_ electionApplyCourseId,
  t1.CALENDAR_ID_ calendarId,
  t1.MODE_ mode,
  t2.CODE_ code,
  t2.NAME_ name,
  t2.NAME_EN_ nameEn,
  t2.TRAINING_LEVEL_ trainingLevel,
  t2.CREDITS_ credits,
  t2.PERIOD_ period,
  t2.WEEK_HOUR_ weekHour,
  t2.COLLEGE_ college,
  t2.NATURE_ nature
FROM
	election_apply_courses_t t1
INNER JOIN course_t t2 ON t1.COURSE_CODE_ = t2.CODE_


        and t1.CALENDAR_ID_ = #{calendarId}


        and t1.MODE_ = #{mode}


        and t2.CODE_ = #{code}


        and t2.NAME_ = #{name}


        and (t2.CODE_ like concat('%', #{keyword},'%') or t2.NAME_ like concat('%', #{keyword},'%') or t2.NAME_EN_ like concat('%', #{keyword},'%'))


        and t2.COLLEGE_ = #{college}


        and t2.PERIOD_ = #{period}


        and t2.CREDITS_ = #{credits}


        and t2.WEEK_HOUR_ = #{weekHour}


        and t2.PERIOD_/t2.WEEK_HOUR_ = #{weekNum}


        and t2.CODE_ in 

	            #{code}

select t2.CODE_ code, t2.NAME_ name, t2.NAME_EN_ nameEn, t2.CREDITS_ credits, t2.ENABLED_ enabled, t2.WEEKS_ weeks, t2.WEEK_HOUR_ weekHour, t2.PERIOD_ period, t2.CROSS_TERM_ crossTerm, t2.TERM_ term, t2.TRAINING_LEVEL_ trainingLevel, t2.FORM_LEARNING_ formLearning, t2.IS_ELECTIVE_ isElective, t2.COLLEGE_ college, t2.LABEL_ label, t2.TEACHING_LANGUAGE_ teachingLanguage, t2.SCORE_TYPE_ scoreType, t2.ASSESSMENT_MODE_ assessmentMode, t2.HEAD_TEACHER_ headTeacher, t2.TEACHERS_ teachers, t2.MANAGER_DEPT_ID_ managerDeptId, t2.STATUS_ status, t2.UPDATE_TIME_ updateTime, t2.REMARK_ remark, t2.TEACH_MODE_ teachMode, t2.NATURE_ nature FROM course_t t2
and t2.STATUS_ = #{status}
and (t2.CODE_ like concat(#{keyword},'%') or t2.NAME_ like concat(#{keyword},'%'))
and t2.CODE_ in
#{code}

select DISTINCT t2.CODE_ code, t2.NAME_ name, t2.NAME_EN_ nameEn, t2.CREDITS_ credits, t2.ENABLED_ enabled, t2.WEEKS_ weeks, t2.WEEK_HOUR_ weekHour, t2.PERIOD_ period, t2.CROSS_TERM_ crossTerm, t2.TERM_ term, t2.TRAINING_LEVEL_ trainingLevel, t2.FORM_LEARNING_ formLearning, t2.IS_ELECTIVE_ isElective, t2.COLLEGE_ college, t2.LABEL_ label, t2.TEACHING_LANGUAGE_ teachingLanguage, t2.SCORE_TYPE_ scoreType, t2.ASSESSMENT_MODE_ assessmentMode, t2.HEAD_TEACHER_ headTeacher, t2.TEACHERS_ teachers, t2.MANAGER_DEPT_ID_ managerDeptId, t2.STATUS_ status, t2.UPDATE_TIME_ updateTime, t2.REMARK_ remark, t2.TEACH_MODE_ teachMode, t2.NATURE_ nature from course_t t2 join course_open_t t1 on t1.COURSE_CODE_ = t2.CODE_ join teaching_task_t t3 ON t3.COURSE_OPEN_ID_ = t1.ID_ join teaching_class_t t4 ON t4.TASK_ID_ = t3.ID_
t1.CALENDAR_ID_ = #{calendarId} and t1.IS_OPEN_ = 1 and t1.PROJ_ID_ = 1 and t1.DELETE_STATUS_ = 0 and t4.DEPLOY_FLAG_ > 0
and t1.FACULTY_ = #{faculty}
and t2.STATUS_ = #{status}
and t2.CODE_ not in (select COURSE_CODE_ from election_apply_courses_t where CALENDAR_ID_ = #{calendarId})
and (t2.CODE_ like concat(#{keyword},'%') or t2.NAME_ like concat(#{keyword},'%'))

select DISTINCT t2.CODE_ code, t2.NAME_ name, t2.NAME_EN_ nameEn, t2.CREDITS_ credits, t2.ENABLED_ enabled, t2.WEEKS_ weeks, t2.WEEK_HOUR_ weekHour, t2.PERIOD_ period, t2.CROSS_TERM_ crossTerm, t2.TERM_ term, t2.TRAINING_LEVEL_ trainingLevel, t2.FORM_LEARNING_ formLearning, t2.IS_ELECTIVE_ isElective, t2.COLLEGE_ college, t2.LABEL_ label, t2.TEACHING_LANGUAGE_ teachingLanguage, t2.SCORE_TYPE_ scoreType, t2.ASSESSMENT_MODE_ assessmentMode, t2.HEAD_TEACHER_ headTeacher, t2.TEACHERS_ teachers, t2.MANAGER_DEPT_ID_ managerDeptId, t2.STATUS_ status, t2.UPDATE_TIME_ updateTime, t2.REMARK_ remark, t2.TEACH_MODE_ teachMode, t2.NATURE_ nature from course_t t2 join course_open_t t1 on t1.COURSE_CODE_ = t2.CODE_ join teaching_task_t t3 ON t3.COURSE_OPEN_ID_ = t1.ID_ join teaching_class_t t4 ON t4.TASK_ID_ = t3.ID_
t1.CALENDAR_ID_ = #{calendarId} and t1.IS_OPEN_ = 1 and t1.PROJ_ID_ = 1 and t1.DELETE_STATUS_ = 0 and t4.DEPLOY_FLAG_ > 0
and t1.FACULTY_ = #{faculty}
and t2.STATUS_ = #{status}
and t2.CODE_ not in (select COURSE_CODE_ from election_apply_courses_t where CALENDAR_ID_ = #{calendarId})
and (t2.CODE_ like concat(#{keyword},'%') or t2.NAME_ like concat(#{keyword},'%'))

SELECT t2.CODE_ code, t2.NAME_ name, t2.NAME_EN_ nameEn, t2.CREDITS_ credits, t2.ENABLED_ enabled, t2.WEEKS_ weeks, t2.WEEK_HOUR_ weekHour, t2.PERIOD_ period, t2.CROSS_TERM_ crossTerm, t2.TERM_ term, t2.TRAINING_LEVEL_ trainingLevel, t2.FORM_LEARNING_ formLearning, t2.IS_ELECTIVE_ isElective, t2.COLLEGE_ college, t2.LABEL_ label, t2.TEACHING_LANGUAGE_ teachingLanguage, t2.SCORE_TYPE_ scoreType, t2.ASSESSMENT_MODE_ assessmentMode, t2.HEAD_TEACHER_ headTeacher, t2.TEACHERS_ teachers, t2.MANAGER_DEPT_ID_ managerDeptId, t2.STATUS_ status, t2.UPDATE_TIME_ updateTime, t2.REMARK_ remark, t2.TEACH_MODE_ teachMode, t2.NATURE_ nature FROM course_t t2
and t2.STATUS_ = #{status}
and (t2.CODE_ like concat(#{keyword},'%') or t2.NAME_ like concat(#{keyword},'%'))
and t2.CODE_ not in
#{code}
