mapper.select:
select

from exemption_course_t


        and CALENDAR_ID_ = #{calendarId,jdbcType=BIGINT}


        and TRAINING_LEVEL_ = #{trainingLevel,jdbcType=VARCHAR}


        and (COURSE_CODE_ like concat('%',#{name},'%') or NAME_ like concat('%',#{name},'%') )


        and FORM_LEARNING_ = #{formLearning,jdbcType=VARCHAR}


        and COURSE_CODE_ = #{courseCode,jdbcType=VARCHAR}

