mapper.select:
select 

from rebuild_course_charge_t


        and TRAINING_LEVEL_ =#{trainingLevel,jdbcType=VARCHAR}


        and FORM_LEARNING_ =#{formLearning,jdbcType=VARCHAR}


order by ID_ DESC

mapper.delete:
delete from rebuild_course_charge_t
where ID_ in

    #{id}

mapper.select:
SELECT re.ID_ id,re.CALENDAR_ID_ calendarId,re.STUDENT_CODE_ studentCode,st.NAME_ studentName,co.ID_ courseId,re.COURSE_CODE_ code,co.NAME_ codeName,co.LABEL_ label,co.CREDITS_ credits FROM `rebuild_course_recycle_t` re LEFT JOIN student_t st on re.STUDENT_CODE_=st.STUDENT_CODE_
LEFT JOIN course_t co on co.CODE_=re.COURSE_CODE_


        and re.CALENDAR_ID_ = #{calendarId,jdbcType=BIGINT}


        and st.STUDENT_CODE_ like CONCAT('%', #{studentCode,jdbcType=VARCHAR}, '%')


        and st.NAME_ like CONCAT('%', #{studentName,jdbcType=VARCHAR}, '%')


        and st.GRADE_ =#{grade,jdbcType=INTEGER}


        and st.TRAINING_LEVEL_ =#{trainingLevel,jdbcType=VARCHAR}


        and st.STUDENT_CATEGORY_ =#{studentCategory,jdbcType=VARCHAR}


        and st.FACULTY_ =#{faculty,jdbcType=VARCHAR}


        and st.PROFESSION_ =#{profession,jdbcType=VARCHAR}


        and st.RESEARCH_DIRECTION_ =#{researchDirection,jdbcType=VARCHAR}


        and co.COLLEGE_ =#{openFaculty,jdbcType=VARCHAR}


        and co.LABEL_ like CONCAT('%', #{label,jdbcType=VARCHAR}, '%')


        and co.ID_ like CONCAT('%', #{courseId,jdbcType=BIGINT}, '%')


        and co.CODE_ like CONCAT('%', #{code,jdbcType=VARCHAR}, '%')


        and co.NAME_ like CONCAT('%', #{name,jdbcType=VARCHAR}, '%')

mapper.delete:
delete from rebuild_course_recycle_t
where ID_ in

    #{item.id}
