mapper.select:
SELECT TURN_ name, NAME_ turnName, ELECTION_OBJ_ electionObj, MODE_ mode FROM election_rounds_t WHERE ID_ IN ( SELECT distinct ifnull (rounds.ID_,null) /*解决子查询查询不到值时，in ()语法错误*/ FROM rebuild_course_recycle_t re left join ( select TURN_,NAME_ , CALENDAR_ID_, ID_, ELECTION_OBJ_ , MODE_, MANAGER_DEPT_ID_, (CASE when ELECTION_OBJ_ = 'STU' then 1 when ELECTION_OBJ_ = 'DEPART_ADMIN' then 2 when ELECTION_OBJ_ = 'ADMIN' then 3 end) CHOOSE_OBJ_ from election_rounds_t where (DELETE_STATUS_ = 0 or DELETE_STATUS_ is null) )rounds on re.CALENDAR_ID_ = rounds.CALENDAR_ID_ and re.TURN_ = rounds.TURN_ and re.CHOOSE_OBJ_ = rounds.CHOOSE_OBJ_ and re.MODE_ = rounds.MODE_
and re.CALENDAR_ID_ = #{calendarId,jdbcType=BIGINT}
and rounds.ELECTION_OBJ_ = #{electionObj,jdbcType=VARCHAR}
and re.MODE_ = #{mode,jdbcType=INTEGER}
and re.TYPE_ = #{type}
and rounds.MANAGER_DEPT_ID_ = #{deptId,jdbcType=VARCHAR}
and re.ID_ in
#{id}
)

SELECT re.ID_ id, re.CALENDAR_ID_ calendarId, te.CODE_ teachingClassCode, re.STUDENT_CODE_ studentCode, re.TEACHING_CLASS_ID_ teachingClassId, re.COURSE_TAKE_TYPE_ courseTakeType, re.TURN_ turn, rounds.NAME_ turnName, re.MODE_ mode, re.CHOOSE_OBJ_ chooseObj, rounds.ELECTION_OBJ_ electionObj, re.PAID_ paid, st.NAME_ studentName, st.GRADE_ grade, st.FACULTY_ faculty, st.PROFESSION_ profession, co.COLLEGE_ courseFaculty, re.COURSE_CODE_ courseCode, co.NAME_ courseName, te.COURSE_LABEL_ID_ label, co.CREDITS_ credits, co.IS_ELECTIVE_ nature, esl.LABEL_NAME_ labelName, CONCAT(te.START_WEEK_,'-',te.END_WEEK_,'周',te.PERIOD_,'学时') courseArr FROM rebuild_course_recycle_t re LEFT JOIN student_t st on re.STUDENT_CODE_=st.STUDENT_CODE_ LEFT JOIN course_t co on co.CODE_=re.COURSE_CODE_ left join teaching_class_t te on te.ID_=re.TEACHING_CLASS_ID_ left join elc_screening_label_t esl on re.CALENDAR_ID_ = esl.CALENDAR_ID_ left join ( select TURN_,NAME_ , CALENDAR_ID_, ELECTION_OBJ_ , MODE_, MANAGER_DEPT_ID_, (CASE when ELECTION_OBJ_ = 'STU' then 1 when ELECTION_OBJ_ = 'DEPART_ADMIN' then 2 when ELECTION_OBJ_ = 'ADMIN' then 3 end) CHOOSE_OBJ_ from election_rounds_t where (DELETE_STATUS_ = 0 or DELETE_STATUS_ is null) )rounds on re.CALENDAR_ID_ = rounds.CALENDAR_ID_ and re.TURN_ = rounds.TURN_ and re.CHOOSE_OBJ_ = rounds.CHOOSE_OBJ_ and re.MODE_ = rounds.MODE_ and st.MANAGER_DEPT_ID_ = rounds.MANAGER_DEPT_ID_
and re.CALENDAR_ID_ = #{calendarId,jdbcType=BIGINT}
and rounds.ELECTION_OBJ_ = #{electionObj,jdbcType=VARCHAR}
and st.STUDENT_CODE_ =#{studentId,jdbcType=VARCHAR}
and st.NAME_ =#{studentName,jdbcType=VARCHAR}
and te.CODE_ =#{teachingClassCode,jdbcType=VARCHAR}
and re.MODE_ = #{mode,jdbcType=INTEGER}
and re.PAID_ = #{paid,jdbcType=INTEGER}
and (st.STUDENT_CODE_ like CONCAT('%', #{keyWord,jdbcType=VARCHAR}, '%') or st.NAME_ like CONCAT('%', #{keyWord,jdbcType=VARCHAR}, '%') )
and st.MANAGER_DEPT_ID_ = #{deptId,jdbcType=VARCHAR}
and te.COURSE_LABEL_ID_ = #{label,jdbcType=VARCHAR}
and co.CODE_ like CONCAT('%', #{courseCode,jdbcType=VARCHAR}, '%')
and co.NAME_ like concat('%',#{courseName,jdbcType=VARCHAR},'%')
and st.GRADE_ = #{grade}
and re.TURN_ = #{turn}
and re.COURSE_TAKE_TYPE_ = #{courseTakeType}
and esl.LABEL_NAME_ = #{labelName,jdbcType=VARCHAR}
and st.FACULTY_ = #{faculty,jdbcType=VARCHAR}
and st.PROFESSION_ = #{profession,jdbcType=VARCHAR}
and co.COLLEGE_ = #{courseFaculty,jdbcType=VARCHAR}
and co.IS_ELECTIVE_ = #{nature,jdbcType=VARCHAR}
and re.TYPE_ = #{type}
and re.ID_ in
#{id}

