mapper.select:
	SELECT 
		t1.STUDENT_CODE_,
		t1.NAME_,
		t1.GRADE_,
		t1.TRAINING_LEVEL_,
		t1.TRAINING_CATEGORY_,
		t1.DEGREE_TYPE_,
		t1.FORM_LEARNING_,
		t1.FACULTY_,
		t1.PROFESSION_
	FROM student_t t1

		t1.LEAVE_SCHOOL_ = 1
		and t1.STUDENT_CODE_  IN  

	          #{studentId}

		AND t1.STUDENT_CODE_  NOT IN (SELECT STUDENT_ID_ FROM elc_course_takes_t t2 WHERE CALENDAR_ID_ = #{query.calendarId} and COURSE_CODE_ = #{query.courseCode})

        and t1.STUDENT_CODE_ like concat('%',#{studentCode},'%')


	  	and t1.GRADE_ = #{query.grade}


	  	and (t1.STUDENT_CODE_ like concat('%',#{query.keyword},'%') or t1.NAME_ like concat('%',#{query.keyword},'%'))


	  	and t1.FACULTY_ = #{query.faculty}


	  	and t1.PROFESSION_ = #{query.profession}


	    and t1.DEGREE_CATEGORY_= #{query.degreeType} 


	    and t1.FORM_LEARNING_= #{query.formLearning} 


	    and t1.TRAINING_LEVEL_= #{query.trainingLevel} 


	    and t1.TRAINING_CATEGORY_= #{query.trainingCategory}

SELECT t1.STUDENT_CODE_, t1.NAME_, t1.TRAINING_LEVEL_, t1.STUDENT_CATEGORY_, t1.FACULTY_, t1.PROFESSION_, t1.RESEARCH_DIRECTION_ FROM student_t t1 WHERE STUDENT_CODE_ NOT IN ( SELECT STUDENT_ID_ FROM elc_affinity_courses_stds_t where COURSE_ID_ =#{courseId} )
and t1.STUDENT_CODE_ like concat('%',#{studentCode},'%')
and t1.NAME_ like concat('%',#{name},'%')
and t1.TRAINING_LEVEL_ = #{trainingLevel}
and t1.FACULTY_ = #{faculty}
and t1.PROFESSION_ = #{profession}
and t1.GRADE_ = #{grade}

SELECT
t2.*
FROM
	elc_invincible_stds_t t1
INNER JOIN student_t t2 ON t1.STUDENT_ID_ = t2.STUDENT_CODE_


        t2.STUDENT_CODE_ =#{studentCode}


        and t2.NAME_ =#{name}


        and t2.MANAGER_DEPT_ID_ =#{managerDeptId}

SELECT

FROM
	student_t t1

	STUDENT_CODE_ NOT IN (
		SELECT
			STUDENT_ID_
		FROM
			elc_invincible_stds_t
	)

	       and  t1.STUDENT_CODE_ =#{studentCode}


	        and t1.NAME_ =#{name}


	        and t1.MANAGER_DEPT_ID_ =#{managerDeptId}

select distinct class.CODE_ teachClassCode, course.NATURE_ nature, course.COURSE_CODE_ courseCode,course.COURSE_NAME_ courseName, course.CREDITS_ credits,teacher.TEACHER_CODE_ teacherCode,teacher.TEACHER_NAME_ teacherName, course.FACULTY_ faculty, class.ID_ teachClassId, class.ELC_NUMBER_ elcNumber,class.NUMBER_ number, class.REMARK_ remark from course_open_t course left join teaching_task_t task on course.ID_ = task.COURSE_OPEN_ID_ left join teaching_class_t class on task.ID_ = class.TASK_ID_ left join teaching_class_teacher_t teacher on class.ID_ = teacher.TEACHING_CLASS_ID_ left join teaching_class_arrange_time_t time ON class.ID_ = time.TEACHING_CLASS_ID_ where time.TYPE_ = '2' and course.PROJ_ID_ = '2' and course.CALENDAR_ID_ = #{calendarId} and course.TRAINING_LEVEL_ = #{trainingLevel}
and class.CAMPUS_ =#{campu,jdbcType=VARCHAR}
and course.COURSE_NAME_ like concat('%',#{courseName},'%')
and teacher.TEACHER_NAME_ like concat('%',#{teacherName},'%')
and course.FACULTY_ =#{faculty,jdbcType=VARCHAR}
and course.NATURE_ =#{nature,jdbcType=VARCHAR}
order by nature

       SELECT t4.* FROM student_t t4 

        	t4.STUDENT_CODE_ NOT IN (SELECT STUDENT_ID_ FROM elc_course_takes_t t2 WHERE CALENDAR_ID_ = #{query.calendarId} GROUP BY t2.STUDENT_ID_)
        	and t4.MANAGER_DEPT_ID_ NOT IN (#{query.managerDeptId}) and t1.LEAVE_SCHOOL_ = 1

		        and t4.FACULTY_ = #{query.faculty}


		        and t4.GRADE_ = #{query.grade}


		        and t4.ENROL_SEASON_ = #{query.enrolSeason}


		        and t4.DEGREE_CATEGORY_= #{query.degreeType}


		        and t4.FORM_LEARNING_ = #{query.formLearning}


		        and t4.TRAINING_LEVEL_ = #{query.trainingLevel}


		        and t4.TRAINING_CATEGORY_ = #{query.trainingCategory}


		        and t4.PROFESSION_ = #{query.profession}


		        and (t4.DEGREE_CATEGORY_ = "" or t4.DEGREE_CATEGORY_ is null)


		        and (t4.FORM_LEARNING_ = "" or t4.FORM_LEARNING_ is null)


		        and (t4.TRAINING_LEVEL_ = "" or t4.TRAINING_LEVEL_ is null)


		        and (t4.TRAINING_CATEGORY_ = "" or t4.TRAINING_CATEGORY_ is null)

       SELECT t4.* FROM student_t t4 

        	t4.STUDENT_CODE_ NOT IN (SELECT STUDENT_ID_ FROM elc_course_takes_t t2 WHERE CALENDAR_ID_ = #{query.calendarId} GROUP BY t2.STUDENT_ID_)
        	and t4.MANAGER_DEPT_ID_ NOT IN (#{query.managerDeptId})

		        and t4.FACULTY_ = #{query.faculty}


		        and t4.GRADE_ = #{query.grade}


		        and t4.ENROL_SEASON_ = #{query.enrolSeason}


		        and t4.DEGREE_CATEGORY_= #{query.degreeType}


		        and t4.FORM_LEARNING_ = #{query.formLearning}


		        and t4.TRAINING_LEVEL_ = #{query.trainingLevel}


		        and t4.TRAINING_CATEGORY_ = #{query.trainingCategory}


		        and t4.PROFESSION_ = #{query.profession}
