,t5.FACULTY_ limitFaculty,t5.TRAINING_LEVEL_ limitTrainingLevel, t5.TRAINING_CATEGORY_ limitTrainingCategory,t5.SPCIAL_PLAN_ limitSpcialPlan, t5.IS_OVERSEAS_ limitIsOverseas,t5.IS_DIVSEX_ limitIsDivsex, t5.NUMBER_MALE_,t5.NUMBER_FEMALE_,t6.GRADE_ limitGrade, t6.PROFESSION_ limitProfession,t6.DIRECTION_CODE_ limitDirectionCode
from teaching_class_t t1 join teaching_task_t t2 ON t2.ID_ = t1.TASK_ID_ join course_open_t t3 ON (t3.ID_ = t2.COURSE_OPEN_ID_ and t3.PROJ_ID_ =1)
left join teaching_class_elective_restrict_attr_t t5 on t5.TEACHING_CLASS_ID_ = t1.ID_ left join teaching_class_elective_restrict_profession_t t6 on t6.TEACHING_CLASS_ID_ =t1.ID_
join teaching_class_arrange_time_t time on (t1.ID_ = time.TEACHING_CLASS_ID_ and time.TYPE_ =2) join teaching_class_arrange_room_t room on room.ARRANGE_TIME_ID_ = time.ID_ LEFT JOIN teaching_class_teacher_t t7 ON t7.TEACHING_CLASS_ID_ = t1.ID_ AND t7.`TYPE_` = 0 left join elc_teaching_class_bind_t t8 on (t8.TEACHING_CLASS_ID_ = t1.ID_) LEFT JOIN teaching_class_t t9 ON (t8.TEACHING_CLASS_ID_ = t9.ID_)
t1.DELETE_STATUS_ = 0 and t3.CALENDAR_ID_ = #{calendarId} and t1.DEPLOY_FLAG_ > 0
and t1.CODE_ = #{teachingClassCode,jdbcType=VARCHAR}
and t1.ID_ != #{teachingClassId}
and t3.IS_ELECTIVE_ = #{isElective}
and t3.TRAINING_LEVEL_ = #{trainingLevel}
and t1.COURSE_LABEL_ID_ = #{courseLabelId}
and t1.ELC_NUMBER_ > t1.NUMBER_
and t1.ELC_NUMBER_ < t1.NUMBER_
and t1.ELC_NUMBER_ = t1.NUMBER_
and t1.NUMBER_ > #{minUpperNumber}
and t1.NUMBER_ < #{maxUpperNumber}
and t1.ELC_NUMBER_ > #{minActualNumber}
and t1.ELC_NUMBER_ < #{maxActualNumber}
and t1.MAN_ARRANGE_FLAG_ = #{manArrangeFlag}
and t3.COURSE_CODE_ = #{courseCode}
and t3.COURSE_NAME_ = #{courseName}
and t1.CAMPUS_ = #{campus}
and t3.PROJ_ID_ = #{projectId}
and t3.FACULTY_ in
#{fy}
and t3.FACULTY_ = #{faculty}
and t3.CREDITS_ = #{credits}
and t3.COURSE_CODE_ in
#{courseCode}
and t1.ID_ in
#{teachingClassId}
and (t1.CODE_ like CONCAT(#{keyword}, '%') or t3.COURSE_CODE_ like CONCAT(#{keyword}, '%') OR t3.COURSE_NAME_ like CONCAT(#{keyword}, '%'))
GROUP BY t1.ID_
HAVING (select count(t4.id_) from teaching_class_teacher_t t4 where t4.TEACHING_CLASS_ID_ = t1.ID_ and t4.TYPE_=0 and t4.TEACHER_CODE_ = #{teacherCode} ) > 0

    select t8.* from (
