       GROUP BY t1.ID_

  	    HAVING (select count(t4.id_) from teaching_class_teacher_t t4
  	    	where t4.TEACHING_CLASS_ID_ = t1.ID_ and t4.TYPE_=0 and t4.TEACHER_CODE_ = #{teacherCode} ) > 0

