	join teaching_task_t t2 ON t2.ID_ = t1.TASK_ID_
	join course_open_t t3 ON t3.ID_ = t2.COURSE_OPEN_ID_

     t1.DEPLOY_FLAG_ > 0

        and t3.CALENDAR_ID_ = #{calendarId}


