SELECT t2.ID_ id, t2.CODE_ code, COUNT(t1.ID_) withdrawNum FROM election_log_t_${index} t1 INNER JOIN teaching_class_t t2 ON t1.TEACHING_CLASS_CODE_ = t2.CODE_ AND t2.DEPLOY_FLAG_ > 0 INNER JOIN teaching_task_t t3 ON t2.TASK_ID_ = t3.ID_ INNER JOIN course_open_t t4 ON ( t4.ID_ = t3.COURSE_OPEN_ID_ AND t1.CALENDAR_ID_ = t4.CALENDAR_ID_ )
