mapper.select:
  select t1.STUDENT_ID_ studentCode,t3.NAME_ studentName,t3.FACULTY_ faculty,t3.PROFESSION_ major,t3.TRAINING_LEVEL_ trainingLevel,
  t3.TRAINING_CATEGORY_ trainingCategory,t3.DEGREE_TYPE_ degreeType,t3.FORM_LEARNING_ formLearning,t3.IS_OVERSEAS_ isOverseas,
  t2.ID_ teachingClassId,t2.NAME_ teachingClassName,t2.CODE_ teachingClassCode,t6.ID_ examInfoId
  from graduate_exam_info_t t6
  left join elc_course_takes_t_${mode} t1 on t1.CALENDAR_ID_ = t6.CALENDAR_ID_ and t1.COURSE_CODE_ = t6.COURSE_CODE_
  left join teaching_class_t t2 on t1.TEACHING_CLASS_ID_ = t2.ID_
  left join student_t t3 on t3.STUDENT_CODE_ = t1.STUDENT_ID_


      and t6.ID_ in

        #{examInfoId}



      and t2.CAMPUS_ = #{campus}


      and t3.FACULTY_ = #{faculty}


      and t3.PROFESSION_ = #{major}


      and t3.TRAINING_LEVEL_ = #{trainingLevel}


      and t3.TRAINING_CATEGORY_ = #{trainingCategory}


      and t3.DEGREE_TYPE_ = #{degreeType}


      and t3.FORM_LEARNING_ = #{formLearning}


      and t3.IS_OVERSEAS_ = #{isOverseas}


      and ( t3.STUDENT_CODE_ like concat('%',#{keyword},'%') or t3.NAME_ like concat('%',#{keyword},'%'))

      and t1.STUDENT_ID_ not in (
        select t4.STUDENT_CODE_
        from graduate_exam_student_t t4
        where t4.EXAM_INFO_ID_ in

          #{examInfoId}

        and t4.EXAM_SITUATION_ = 1
        )
    and t1.STUDENT_ID_ not in (
    select t7.STUDENT_ID_ from score_student_result_t t7
    where t7.STUDENT_ID_ = t1.STUDENT_ID_ and t7.CALENDAR_ID_ = t6.CALENDAR_ID_ and t7.COURSE_CODE_ = t6.COURSE_CODE_ and t7.RELEASE_TYPE_ = 1
    )

ORDER BY t6.COURSE_CODE_ asc ,t2.CODE_ asc

  select t1.STUDENT_CODE_ studentCode,t3.NAME_ studentName,t3.FACULTY_ faculty,t3.PROFESSION_ major,t3.TRAINING_LEVEL_ trainingLevel,
  t3.TRAINING_CATEGORY_ trainingCategory,t3.DEGREE_TYPE_ degreeType,t3.FORM_LEARNING_ formLearning,t3.IS_OVERSEAS_ isOverseas,
  t2.ID_ teachingClassId,t2.NAME_ teachingClassName,t2.CODE_ teachingClassCode,t6.ID_ examInfoId
  from graduate_exam_info_t t6
  left join graduate_exam_apply_examination_t t1 on t1.CALENDAR_ID_ = t6.CALENDAR_ID_ and t1.COURSE_CODE_ = t6.COURSE_CODE_
  left join teaching_class_t t2 on t1.TEACHING_CLASS_ID_ = t2.ID_
  left join student_t t3 on t3.STUDENT_CODE_ = t1.STUDENT_CODE_


      and t6.ID_ in

        #{examInfoId}



      and t2.CAMPUS_ = #{campus}


      and t3.FACULTY_ = #{faculty}


      and t3.PROFESSION_ = #{major}


      and t3.TRAINING_LEVEL_ = #{trainingLevel}


      and t3.TRAINING_CATEGORY_ = #{trainingCategory}


      and t3.DEGREE_TYPE_ = #{degreeType}


      and t3.FORM_LEARNING_ = #{formLearning}


      and t3.IS_OVERSEAS_ = #{isOverseas}


      and ( t3.STUDENT_CODE_ like concat('%',#{keyword},'%') or t3.NAME_ like concat('%',#{keyword},'%'))


    and t1.STUDENT_CODE_ not in (
    select t4.STUDENT_CODE_
    from graduate_exam_student_t t4
    where t4.EXAM_INFO_ID_ in

      #{examInfoId}

    and t4.EXAM_SITUATION_ = 1
    )
    and t1.APPLY_STATUS_ = 4

ORDER BY t6.COURSE_CODE_ asc ,t2.CODE_ asc

mapper.update:
update graduate_exam_student_t set EXAM_SITUATION_ = #{applyType} , REMARK_ = #{aduitOpinions}
where ID_ in

  #{item.id}

mapper.select:
select t2.ID_ examInfoId,t2.CAMPUS_ campus,t2.COURSE_CODE_ courseCode,t2.EXAM_DATE_ examDate,t2.EXAM_TIME_ examTime, t2.EXAM_START_TIME_ examStartTime,t2.EXAM_END_TIME_ examEndTime,t1.STUDENT_CODE_ studentCode,t3.NAME_ courseName from graduate_exam_student_t t1 left join graduate_exam_info_t t2 on t1.EXAM_INFO_ID_ = t2.ID_ left join course_t t3 on t2.COURSE_CODE_ = t3.CODE_ where t1.STUDENT_CODE_ in
#{item.studentCode}
and t2.CALENDAR_ID_ = #{calendarId} and t1.EXAM_SITUATION_ = 1

select DISTINCT t3.STUDENT_CODE_ studentCode,t3.NAME_ studentName from graduate_exam_student_t t1 left join graduate_exam_info_t t2 on t1.EXAM_INFO_ID_ = t2.ID_ left join student_t t3 on t3.STUDENT_CODE_ = t1.STUDENT_CODE_
and ( t3.STUDENT_CODE_ like concat('%',#{keyword},'%') or t3.NAME_ like concat('%',#{keyword},'%'))
and t3.FACULTY_ in
#{faculty}
and t2.CALENDAR_ID_ = #{calendarId} and t2.EXAM_TYPE_ = 1 and t1.EXAM_SITUATION_ = 1 and t2.PROJ_ID_ = #{projId}

select DISTINCT t2.STUDENT_CODE_ studentCode,t2.NAME_ studentName from score_student_result_t t1 left join student_t t2 on t2.STUDENT_CODE_ = t1.STUDENT_ID_ left join course_t t3 on t1.COURSE_CODE_ = t3.CODE_
and ( t2.STUDENT_CODE_ like concat('%',#{keyword},'%') or t2.NAME_ like concat('%',#{keyword},'%'))
and t2.FACULTY_ in
#{faculty}
and t1.CALENDAR_ID_ = #{calendarId} and t1.IS_PASS_ = 0 and t1.RELEASE_TYPE_ = 1 and t3.MANAGER_DEPT_ID_ = #{projId}

select t3.CODE_ courseCode,t3.NAME_ courseName from graduate_exam_student_t t1 left join graduate_exam_info_t t2 on t1.EXAM_INFO_ID_ = t2.ID_ left join course_t t3 on t2.COURSE_CODE_ = t3.CODE_
and ( t3.CODE_ like concat('%',#{keyword},'%') or t3.NAME_ like concat('%',#{keyword},'%'))
and t2.CALENDAR_ID_ = #{calendarId} and t2.EXAM_TYPE_ = 1 and t1.EXAM_SITUATION_ = 1 and t2.PROJ_ID_ = #{projId} and t1.STUDENT_CODE_ = #{studentCode}

select t2.CODE_ courseCode,t2.NAME_ courseName from score_student_result_t t1 left join course_t t2 on t1.COURSE_CODE_ = t2.CODE_
and ( t2.CODE_ like concat('%',#{keyword},'%') or t2.NAME_ like concat('%',#{keyword},'%'))
and t1.IS_PASS_ = 0 and t1.RELEASE_TYPE_ = 1 and t1.STUDENT_ID_ = #{studentCode} and t1.CALENDAR_ID_ = #{calendarId} and t2.MANAGER_DEPT_ID_ = #{projId}

select t6.ID_ examInfoId,t1.STUDENT_ID_ studentCode,t1.TEACHING_CLASS_ID_ teachingClassId,t5.NAME_ teachingClassName,t5.CODE_ teachingClassCode
from elc_course_takes_t t1
left join teaching_class_t t5 on t1.TEACHING_CLASS_ID_ = t5.ID_
left join graduate_exam_info_t t6 on t1.CALENDAR_ID_ = t6.CALENDAR_ID_ and t1.COURSE_CODE_ = t6.COURSE_CODE_ and t5.CAMPUS_ = t6.CAMPUS_
where t1.TEACHING_CLASS_ID_ in

  #{teachingClassId}

and t1.CALENDAR_ID_ = #{calendarId}
and t1.STUDENT_ID_ not in (
select t4.STUDENT_CODE_
from graduate_exam_room_t t3
left join graduate_exam_student_t t4 on t3.ID_ = t4.EXAM_ROOM_ID_
where t3.ID_ in

  #{examRoomId}

and t4.EXAM_SITUATION_ = 1
)
and t6.EXAM_TYPE_ = #{examType}
and t1.STUDENT_ID_ not in (
select t7.STUDENT_ID_ from score_student_result_t t7
where t7.STUDENT_ID_ = t1.STUDENT_ID_ and t7.CALENDAR_ID_ = t6.CALENDAR_ID_ and t7.COURSE_CODE_ = t6.COURSE_CODE_ and t7.RELEASE_TYPE_ = 1
)

SELECT t5.EXAM_INFO_ID_ examInfoId,t5.ID_ teachingClassId,t5.CODE_ teachingClassCode,t5.NAME_ teachingClassName,t5.COLLEGE_ faculty,t5.CAMPUS_ campus, t5.ELC_NUMBER_ totalNumber,GROUP_CONCAT(t6.TEACHER_CODE_) teacherCode,GROUP_CONCAT(t6.TEACHER_NAME_) teacherName FROM ( SELECT t3.ID_, t3.CODE_, t3.NAME_, t4.COLLEGE_, t3.CAMPUS_, t1.ID_ EXAM_INFO_ID_, COUNT(t2.ID_) ELC_NUMBER_ FROM graduate_exam_info_t t1 LEFT JOIN elc_course_takes_t_${mode} t2 ON t1.CALENDAR_ID_ = t2.CALENDAR_ID_ AND t1.COURSE_CODE_ = t2.COURSE_CODE_ LEFT JOIN teaching_class_t t3 ON t2.TEACHING_CLASS_ID_ = t3.ID_ LEFT JOIN course_t t4 ON t1.COURSE_CODE_ = t4.CODE_ WHERE t1.ID_ in
#{examInfoId}
AND t3.CAMPUS_ = t1.CAMPUS_ GROUP BY t2.TEACHING_CLASS_ID_ ) t5 LEFT JOIN teaching_class_teacher_t t6 ON t5.ID_ = t6.TEACHING_CLASS_ID_ GROUP BY t6.TEACHING_CLASS_ID_
